import { View, TextInput, Button, StyleSheet } from 'react-native'
import React, { useState } from 'react'
import axios from 'axios'

export default function TextBox({navigation}) {
    const [TextValue, setTextValue] = useState('')
    const onsubmit = () => {
        console.log(TextValue);
        axios.get(`https://pokeapi.co/api/v2/pokemon/${TextValue.toLowerCase()}`).then((response) => {
        const data=response.data;
        console.log(data);
        navigation.navigate('Details',{value:data})
        })
    }
    return (

        <View>
            <View>
                <TextInput onChangeText={setTextValue} value={TextValue} placeholder="Enter Pokemon's Name" />
            </View>
            <View>
                <Button title='submit' onPress={onsubmit} disabled={!TextValue?true:false}/>
            </View>
        </View>

    )
};
const styles = StyleSheet.create({
    TitleBox: {
        flex: 1,
        textAlign: 'center',
        justifyContent: "center",
        marginBottom: 20,
    },

})
