import { View, Text,Image ,SafeAreaView,FlatList,StyleSheet,TouchableOpacity} from 'react-native'
import React,{useState,useEffect} from 'react';
import axios from 'axios';
import TextBox from '../components/TextBox';
import MyComponent from '../components/CardBox';


export default function FirstPage({navigation}) {
    const [Imagevalues,setImageValues]=useState([])
    const OnRendering=()=>{
        axios.get("https://pokeapi.co/api/v2/pokemon?limit=20&offset=0").then((response) =>{
            const data=response.data.results;
            data.map(UrlForImage);
        })
    } 
    const UrlForImage=(data)=>{
        axios.get(`${data.url}`).then((response) =>{
            const imageValue=response.data.sprites.other.home.front_default;
            setImageValues(prev=>[...prev,imageValue])
        })
    }
    useEffect(() => {
    }, [Imagevalues])
    useEffect(() => {
        OnRendering();
    }, []);

    const renderItem=({item,index}) =>(
        <View style={styles.Card}>
                      <TouchableOpacity onPress={ontouchingIcon}>
            <MyComponent data={[item,index]}  />
            </TouchableOpacity>
            {/* <Image style={styles.imageValue} source={{uri:item}}/> */}
        </View>
    );

    const ontouchingIcon=()=>{
        console.log("touchable")
    }
  return (
      <SafeAreaView>
          <View>
              <TextBox navigation={navigation}/>
              <FlatList numColumns={3} data={Imagevalues} renderItem={renderItem} />
          </View>
    </SafeAreaView>
  )
}
const styles=StyleSheet.create({
    Card:{
        flex:1,
        alignItems:'center',
        justifyContent:'center',
        padding:100
    },
    imageValue:{
        height:100,
        width:100,
    }
})
